import styled from 'styled-components'


const Wrapper = styled.div`
    width: 20%;
    height: 300px;
    border-radius: 10px;
    background:grey;
    box-shadow: 0px 2px 5px 1px rgb(0 0 0 / 20%);
`;

const Img = styled.img`
    width:100%;
    height:200px;
    object-fit: cover;
`;

const Div = styled.div`
    background: white;
    height: 100px;
`;

export default function Card({ loan }) {
    return (
        <Wrapper>
            <Img src={loan.url} alt={loan.name}/>
            <Div>{loan.name}</Div>
        </Wrapper>
    )
}
