import styled from 'styled-components'


const Wrapper = styled.div`
    width: 20%;
    height: 300px;
    border-radius: 10px;
    background:grey;
    overflow: hidden;
    box-shadow: 0px 2px 5px 1px rgb(0 0 0 / 20%);
`;

const ImgDiv = styled.div`
    width:100%;
    height:200px;
`;

const Img = styled.img`
    width:100%;
    height:200px;
    object-fit: cover;
`;

const Div = styled.div`
    background: white;
    height: 100px;
    text-align:center;
    padding: 20px;
`;

const Link = styled.a`
    text-decoration: none;
    color: white;
    background: blue;
    padding: 10px 30px;
    display: inline-block;
    border-radius: 20px;
`;

const Description = styled.div`
    color: black;
    padding: 10px 20px;
`;

export default function Card({ loan }) {
    return (
        <Wrapper>
            <ImgDiv><Img src={loan.url} alt={loan.name} /></ImgDiv>
            <Div>
                <Link href={'#'}>{loan.name}</Link>
                <Description>{loan.description}</Description>
            </Div>
        </Wrapper>
    )
}
