import { useState } from 'react';
import styled from 'styled-components'

import Card from "../components/Card";

const Wrapper = styled.div`
    padding:50px 80px;
`;

const Head = styled.h3`
    text-align: center;
    color: grey;
`;


const Div = styled.div`
    padding: 20px 0;
    display: flex;
    align-items: center;
    gap: calc(12%/3);
`;


const data = [
    {
        name: 'Easy Ride',
        url: '',
        description: 'Two wheeler Loan'
    }, {
        name: 'Yohavahana',
        url: '',
        description: 'Four wheeler Loan'
    },
    {
        name: 'Easy Ride',
        url: '',
        description: 'Two wheeler Loan'
    }, {
        name: 'Yohavahana',
        url: '',
        description: 'Four wheeler Loan'
    },
]

export default function Products() {

    const [products, setProducts] = useState(data)

    useEffect(() => {
        fetch(`${process.env.REACT_APP_API_URL}v0/productList`, { method: 'POST', body:JSON.stringify({white_label_id: 31}) })
            .then((res) => res.json())
            .then((res) => {
                console.log(res);
                useDetails(res.permission);
            });
    }, []);
    return (
        <Wrapper>
            <Head>
                Select a Loan Product
            </Head>
            <Div>
                {products.map(product => (
                    <Card product={product} />
                ))}
            </Div>

        </Wrapper>
    );
}
