import { useHistory } from 'react-router-dom';
import { string } from 'prop-types';
import styled from 'styled-components'

import { v4 as uuidv4 } from 'uuid';

import { PRODUCT_LIST_URL } from '../config';
import useFetch from '../hooks/useFetch';


const Wrapper = styled.div`
    width: 100%;
    height: 100%;
    display: flex;
`;

const Colom1 = styled.div`
    width: 25%;
    background: ${({ theme }) => theme.buttonColor1};
    color: ${({ theme }) => theme.themeColor1};
    padding: 50px 20px;
`;

const Colom2 = styled.div`
    background: ${({ theme }) => theme.themeColor1};
`;

const Colom3 = styled.div`
    width: 25%;
    background: ${({ theme }) => theme.themeColor1};
`;

const Head = styled.h4`
    border: ${({ active }) => active ? '1px solid' : 'none'};
    border-radius: 10px;
    padding: 15px 20px;
    margin: 10px 0;
`;

const Menu = styled.h5`
    border: ${({ active }) => active ? '1px solid' : 'none'};
    border-radius: 10px;
    padding: 15px 20px;
    margin: 10px 0;
    position: relative;

    &::before{
        ${({ completed }) => completed && `
            content:'';
        `}
        position: absolute;
        top: 50%;
        right: 10px;
        width: 20px;
        height: 20px;
        background: white;
        border-radius: 20px;
        transform: translateY(-50%);
    }

    &::after{
        ${({ completed }) => completed && `
            content:'';
        `}
        position: absolute;
        top: 50%;
        right: 10px;
        width: 4px;
        height: 11px;
        background: transparent;
        border-bottom: 2px solid blue;
        border-right: 2px solid blue;
        transform: translate(-100%, -50%) rotate(45deg);
    }
`;

export default function Product({ product, page, url }) {

    const { response } = useFetch({
        url: `${PRODUCT_LIST_URL}?white_label_id=${32}&product_id=${atob(product)}`,
        options: { method: 'GET' }
    })

    const history = useHistory();

    const handleClick = (e, url) => {
        e.preventDefault();
        history.push(url);
    }
    return (
        <Wrapper>
            <Colom1>
                <a
                    href={`/product/${product}/1`}
                    onClick={(e) => handleClick(e, `/product/${product}/1`)}>
                    <Head active={page === '1'}>
                        Easy Ride <small>(Two wheeler loan)</small>
                    </Head>
                </a>
                {
                    response?.data?.product_details?.step.map(m => (
                        <a
                            href={`/product/${product}/${m.page}`} key={uuidv4()}
                            onClick={(e) => handleClick(e, `/product/${product}/${m.page}`)}>
                            <Menu
                                active={page === m.page.toString()}
                                completed={!!m.subStep}
                            >{m.name}
                            </Menu>
                        </a>

                    ))
                }
            </Colom1>
            <Colom2>Colom 2</Colom2>
            <Colom3>Colom 3</Colom3>
        </Wrapper>)
}

Product.propTypes = {
    product: string.isRequired
};
