import { useState } from 'react';
import styled from 'styled-components'

import Card from "../components/Card";

const Wrapper = styled.div`
    padding:50px 80px;
`;

const Head = styled.h3`
    text-align: center;
    color: grey;
`;


const Div = styled.div`
    padding: 20px 0;
    display: flex;
    align-items: center;
    gap: calc(8%/3);
`;


const data = [
    {
        name: 'Easy Ride',
        url: '',
        description: 'Two wheeler Loan'
    }, {
        name: 'Yohavahana',
        url: '',
        description: 'Four wheeler Loan'
    },
    {
        name: 'Easy Ride',
        url: '',
        description: 'Two wheeler Loan'
    }, {
        name: 'Yohavahana',
        url: '',
        description: 'Four wheeler Loan'
    },
]

export default function Loans() {

    const [loans, setLoans] = useState(data)
    return (
        <Wrapper>
            <Head>
                Select a Loan Product
            </Head>
            <Div>
                {loans.map(loan => (
                    <Card loan={loan} />
                ))}
            </Div>

        </Wrapper>
    );
}
