import { useHistory } from 'react-router-dom';
import { string } from 'prop-types';
import styled from 'styled-components'

import { v4 as uuidv4 } from 'uuid';


const Wrapper = styled.div`
    width: 100%;
    height: 100%;
    display: flex;
`;

const Colom1 = styled.div`
    width: 25%;
    background: ${({ theme }) => theme.buttonColor1};
    color: ${({ theme }) => theme.themeColor1};
    padding: 50px 20px;
`;

const Colom2 = styled.div`
    width: 50%;
    background: ${({ theme }) => theme.themeColor1};
`;

const Colom3 = styled.div`
    width: 25%;
    background: ${({ theme }) => theme.themeColor1};
`;

const Head = styled.h4`
    border: ${({ active }) => active ? '1px solid' : 'none'};
    border-radius: 10px;
    padding: 10px 20px;
    margin: 10px 0;
`;

const Menu = styled.h5`
    border: ${({ active }) => active ? '1px solid' : 'none'};
    border-radius: 10px;
    padding: 10px 20px;
    margin: 10px 0;
`;

const menu = [
    {
        name: "Identity Verification",
        page: 2
    },
    {
        name: "Personal Details",
        page: 3
    },
    {
        name: "Address Details",
        page: 4,
        subMenu: [
            {
                name: 'Co-applicant Details',
                page: 1
            },
            {
                name: 'Co-applicant Income Details',
                page: 2
            },
            {
                name: "Co-applicant Document Upload",
                page: 3
            }
        ]
    },
    {
        name: "Loan Details",
        page: 5
    },
    {
        name: "Document Upload",
        page: 6
    },
    {
        name: "Application Submitted",
        page: 7
    },
]

export default function Product({ product, page, url }) {

    const history = useHistory();

    const handleClick = (e, url) => {
        e.stopPropagation();
        history.push(url);
    }
    return (
        <Wrapper>
            <Colom1>
                <a
                    href={`/product/${product}/1`}
                    onClick={(e) => handleClick(e, `/product/${product}/1`)}>
                    <Head active={page === '1'}>
                        Easy Ride <small>(Two wheeler loan)</small>
                    </Head>
                </a>
                {
                    menu.map(m => (
                        <div
                            key={uuidv4()}
                            onClick={(e) => handleClick(e, `/product/${product}/${m.page}`)}>
                            <Menu active={page === m.page.toString()}>{m.name}
                            </Menu>
                        </div>

                    ))
                }
            </Colom1>
            <Colom2>Colom 2</Colom2>
            <Colom3>Colom 3</Colom3>
        </Wrapper>)
}

Product.propTypes = {
    product: string.isRequired
};
